package lab1111;


public class cocktailshaker {

	public static void main(String[] args) {

		 int[] arr = {5, 4, 1, 2};


	        cocktailSort(arr);

	        System.out.println("ouput:");
	        printArray(arr);


	}

	public static void cocktailSort(int[] arr) {


		        cocktailRecursive(arr, 0, arr.length - 1);
		    }

		    private static void cocktailRecursive(int[] arr, int baslangic, int bitis) {

		        if (baslangic >= bitis) {
		            return;
		        }

		        boolean changed = false;


		        for (int i = baslangic; i < bitis; i++) {
		            if (arr[i] > arr[i + 1]) {
		                change(arr, i, i + 1);
		                changed = true;
		            }
		        }


		        if (!changed) {
		            return;
		        }


		        changed = false;
		        for (int i = bitis - 1; i > baslangic; i--) {
		            if (arr[i] < arr[i - 1]) {
		                change(arr, i, i - 1);
		                changed = true;
		            }
		        }


		        cocktailRecursive(arr, baslangic + 1, bitis - 1);
		    }


		    private static void change(int[] arr, int i, int j) {
		        int x = arr[i];
		        arr[i] = arr[j];
		        arr[j] = x;
		    }




		    private static void printArray(int[] arr) {
		        for (int num : arr) {
		            System.out.print(num + " ");
		        }
		        System.out.println();
		    }


	}

