public class lab1q2 {
    public static void main(String[] args) {
        int arr[] = { 5, 4, 1, 2 };
        cocktailSort(arr, true);
        for (int i = 0; i < arr.length; i++) {
            System.out.println(arr[i]);
        }

    }

    public static void cocktailSort(int[] arr, boolean x) {

        for (int i = 0; i < arr.length - 1; i++) {
            if (arr[i] > arr[i + 1]) {
                int temp = arr[i];
                arr[i] = arr[i + 1];
                arr[i + 1] = temp;
            }
        }
        if (arr.length < 2)
            return;
        {
            if (x) {
                arr = bubble1(arr);
                x = false;
            } else {
                arr = bubble2(arr);
                x = true;
            }
            cocktailSort(arr, x);
        }
    }

    public static int[] bubble1(int[] arr) {
        int newarr[] = new int[arr.length - 1];
        for (int i = 0; i < arr.length - 1; i++) {
            newarr[i] = arr[i];
        }
        for (int i = 0; i < newarr.length - 1; i++) {
            if (newarr[i] > newarr[i + 1]) {
                int temp = newarr[i];
                newarr[i] = newarr[i + 1];
                newarr[i + 1] = temp;
            }
        }
        return newarr;
    }

    public static int[] bubble2(int[] arr) {

        int newarr[] = new int[arr.length - 1];
        for (int i = 0; i < arr.length - 1; i++) {
            newarr[i] = arr[i];
        }
        for (int i = newarr.length - 1; i > 0; i--) {
            if (newarr[i] < newarr[i - 1]) {
                int temp = newarr[i];
                newarr[i] = newarr[i - 1];
                newarr[i - 1] = temp;
            }
        }
        return newarr;

    }

}

