package lab1;

public class lab1q2 { 

    public static void cocktailSort(int[] arr) {
        cocktailSortRecursive(arr, 0, arr.length - 1);
    }

    private static void cocktailSortRecursive(int[] arr, int start, int end) {
        if (start >= end) {
        	return;
        }
        boolean flag = false;


        for (int i = start; i < end; i++) {
            if (arr[i] > arr[i + 1]) { // Bubble sort önden
                int temp = arr[i];
                arr[i] = arr[i + 1];
                arr[i + 1] = temp;
                flag = true; 
            }
        }

        if (!flag) { // Eğer değişim olmadıysa işlem yapılmayacak 
        	return;
        }

        end--; 


        for (int i = end; i > start; i--) {  // Bubble sort arkadan
            if (arr[i] < arr[i - 1]) {
                int temp = arr[i];
                arr[i] = arr[i - 1];
                arr[i - 1] = temp;
                flag = true;
            }
        }

        start++;

        cocktailSortRecursive(arr, start, end);
    }

    public static void main(String[] args) {
        int[] arr = {5, 4, 1, 2};
        cocktailSort(arr);

        for (int num : arr) {
            System.out.print(num + " "); 
        }
    }
}

