public class CocktailShaker{
    public static void cocktailSort(int[] array2){
        csRec(array2, 0, array2.length - 1);
    }
    public static void csRec(int[] array1, int ilk, int son){
        if (ilk >= son) //BASE CASE
            return;
        boolean degistir = false;
        for (int i=ilk ; i<son ; i++){//Soldan sağa doğru kontrol ediyorum
            if (array1[i] > array1[i+1]){ //buradaki durum 5,4 durumu. Bunu 4,5 yapmaya çalışıyorum
                yer_degistirme(array1, i, i+1); //soldaki ile sağdakinin yerini değiştirdim
                degistir = true; //değiştirme işleminin yapıldığını göstermek için
            }
        }
        if (!degistir){ //değiştirme işleminin yani yer_degistirme metodunun kullanılıp kullanılmadığını kontrol ediyorum 
            /*
            eğer değiştirme işlemi yapılsaydı "degistir" değeri true olacaktı ve bu blok false olacağı için çalışmazdı
            değiştirme işlemi yapılmazsa "degistir" değeri false olarak kalacağı için blok true olur ve çalışır.
            */
            return;
        }
        degistir = false;
        for (int i = son-1 ; i>ilk ; i--){//Sağdan sola doğru kontrol ediyorum
            if (array1[i-1] > array1[i]){
                yer_degistirme(array1, i-1, i);//sağdaki ile soldakinin yerini değiştirdim
                degistir = true; //değiştirme işleminin yapıldığını göstermek için
            }
        }
        csRec(array1, ilk+1, son-1);//arrayın ilk elemanı ile son elemanını attım
    }
    public static void yer_degistirme(int[] arr, int a, int b){
        int temp = arr[a];
        arr[a] = arr[b];
        arr[b] = temp;
    }
    public static void main(String[] args){
        int[] array3 = {5, 4, 1, 2};
        cocktailSort(array3);
        for (int a: array3){
            System.out.print(a + " ");
        }
    }
}

