public class LAB211Q2 {
    public static void main(String[] args) {
        int[] arr = { 5, 4, 1, 2 };
        coctailSort(arr);
    }

    public static void coctailSort(int[] arr) {
        int high = arr.length - 1;
        int low = 0;
        int[] tempArr = arr;
        for (int i = 0; i < arr.length; i++) {

            for (int j = 0; j < arr.length - 1; j++) {
                if (tempArr[j] > tempArr[j + 1]) {
                    int temp = tempArr[j];
                    tempArr[j] = tempArr[j + 1];
                    tempArr[j + 1] = temp;

                }
            }

        }

        int count = 0;
        for (int i = 0; i < arr.length - 1; i++) {
            if (arr[i] == tempArr[i]) {
                count++;
            }

        }
        if (count == arr.length) {
            System.out.println(arr);
        } else {
            listingUp(arr, tempArr, low, high);
        }

    }

    public static int listingUp(int[] arr, int[] tempArr, int low, int high) {

        int count = 0;
        for (int i = 0; i < arr.length - 1; i++) {
            if (arr[i] == tempArr[i]) {
                count++;
            }

        }
        if (count == arr.length) {
            coctailSort(arr);
        }

        if (low == high) {
            listingDown(arr, tempArr, 0, high);
        }
        if (arr[low] > arr[low + 1]) {
            int temp = arr[low];
            arr[low] = arr[low + 1];
            arr[low + 1] = temp;

        }
        return listingUp(arr, tempArr, low + 1, high);

    }

    public static int listingDown(int[] arr, int[] tempArr, int low, int high) {
        int count = 0;
        for (int i = 0; i < arr.length - 1; i++) {
            if (arr[i] == tempArr[i]) {
                count++;
            }

        }
        if (count == arr.length) {
            coctailSort(arr);
        }
        if (low == high) {
            listingUp(arr, tempArr, low, arr.length - 1);
        }
        if (arr[high] < arr[high - 1]) {
            int temp = arr[high];
            arr[high] = arr[high - 1];
            arr[high - 1] = temp;

        }
        return listingDown(arr, tempArr, low, high - 1);
    }
}

