            return;
        if (arr[i] > arr[i + 1]) {
            int temp = arr[i];
            arr[i] = arr[i + 1];
            arr[i + 1] = temp;
        }
        helper(i + 1, arr);
    }

    public static void helper2(int i, int[] arr) {
        if (i == 0) // eger ilk elemana geldiysek - base
            return;
        if (arr[i - 1] > arr[i]) {
            int temp = arr[i];
            arr[i] = arr[i - 1];
            arr[i - 1] = temp;
        }
        helper2(i - 1, arr);

    }

    public static void cocktailSort(int[] arr) {
        for (int i = 0; i < arr.length / 2; i++) {
            helper(i, arr);
            helper2(arr.length - 1 - i, arr);
        }
    }
}

