    public static int collatz(int i) {
        if (i == 1) {
            return 0; // 1'e ulaştığımızda adım sayısı 0'dır.
        }
        if (i % 2 == 0) {
            return 1 + collatz(i / 2);
        } else {
            return 1 + collatz(3 * i + 1);
        }
    }

    public static void cocktailSort(int[] arr) {
        cocktailSort2(arr, 0, arr.length - 1);
    }

    public static void cocktailSort2(int[] arr, int start, int end) {
        if (start >= end) {
            return; // işlem tamam
        }

        boolean degistiMi = false;

        // Soldan sağa bubble sort
        for (int i = start; i < end; i++) {
            if (arr[i] > arr[i + 1]) {
                int temp = arr[i];
                arr[i] = arr[i + 1];
                arr[i + 1] = temp;
                degistiMi = true;
            }
        }

        // degistiMi = false ise dizi sıralıdır
        if (!degistiMi) {
            return;
        }

        degistiMi = false;

        // Sağdan sola bubble sort
        for (int i = end - 1; i > start; i--) {
            if (arr[i] < arr[i - 1]) {
                int temp = arr[i];
                arr[i] = arr[i - 1];
                arr[i - 1] = temp;
                degistiMi = true;
            }
        }

        // BAŞ VE SON HARİCİ TEKRAR BAK
        cocktailSort2(arr, start + 1, end - 1);
    }

    public static void printArr(int[] arr) {
        for (int i : arr) {
            System.out.print(i + " ");
        }
        System.out.println();
    }
}

