package test;
public class Cocktail{
    public static void main(String[] args){
        int[] arr = {5,4,1,2};
        cocktailSort(arr);
        //printer
        for (int i = 0; i < arr.length; i++){
            System.out.print(arr[i] + ",");
    }
    }

    public static void cocktailSort(int[] arr){
        bubble(arr, 0, arr.length - 1, false);
    }

    public static void bubble(int[] arr, int start, int end, boolean rightway){
        rightway = true;
        if (start == end && !isOrdered(arr) && rightway){
            rightway = false;
            bubble(arr, arr.length - 1, 0, rightway);
            return;
        }
        if (start == end && !isOrdered(arr) && !rightway){
            rightway = true;
            bubble(arr, 0, arr.length, rightway);
            return;
        }
        else if (start == end && isOrdered(arr))
        {
            return;
        }
        else if (end > start) {

            if (arr[start] > arr[start + 1]){
                int temp = arr[start + 1];
                arr[start + 1] = arr[start];
                arr[start] = temp; 
            }
            bubble(arr, start + 1, end, rightway);
            return;
        }
        else if (start > end) {
            if (arr[start - 1] > arr[start]){
                int temp = arr[start - 1];
                arr[start - 1] = arr[start];
                arr[start] = temp;    
            }
            bubble(arr, start - 1, end, rightway);
            return;
        }   
    }

    public static boolean isOrdered(int[] arr){
        for (int i = 0; i < arr.length - 1; i++)
        {
            if (arr[i] > arr[i + 1]) return false;
        }
        return true;
    }
}

