        System.out.println(s1.toString() + ": " + s1.say()); // 2 
        System.out.println(s2.toString() + ": " + s2.say()); // 4 
        System.out.println(s3.toString() + ": " + s3.say()); // 7

        System.out.println();
    }

//-------------------------------------------------------------------------------------------

    public static int collatz(int i){

        if(i == 1){
            System.out.print(i + " -> ");
            return 0;
        }
        else{
            if(i % 2 == 0){
                System.out.print(i + ", ");
                return 1 + collatz(i / 2);
            }
            else{
                System.out.print(i + ", ");
                return 1 + collatz((i * 3) + 1);
            }
        }
    }

//-------------------------------------------------------------------------------------------  

    public static void cocktailSort(int[] arr) {
        cocktailSortRecursive(arr, 0, arr.length - 1, true);
    }

    public static void cocktailSortRecursive(int[] arr, int left, int right, boolean forward) {

        if (left >= right) {
            return;
        }

        boolean swapped = false;

        if (forward) {
            for (int i = left; i < right; i++) {
                if (arr[i] > arr[i + 1]) {
                    swap(arr, i, i + 1);
                    swapped = true;
                }
            }
            if (!swapped) { return; }

            cocktailSortRecursive(arr, left, right - 1, false);

        } 
        else {
            for (int i = right; i > left; i--) {
                if (arr[i] < arr[i - 1]) {
                    swap(arr, i, i - 1);
                    swapped = true;
                }
            }

            if (!swapped) { return; }

            cocktailSortRecursive(arr, left + 1, right, true);
        }
    }

    private static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

//-------------------------------------------------------------------------------------------

