    public static int collatz(int i)
    {
        if(i == 1)
        {
            return 0 ;  // 1 oldugu zaman 0 donerek cikmasi icin bir base case yazdim
        }
        else
        {
            if(i%2 == 0)
            {
                return 1 + collatz(i/2);  // 2 ile bolumunden kalan 0 ise adim sayisi 1 artarak 2ye bolunmus hali gelecek
            }
            else
            {
                return 1+ collatz((i*3) + 1); // 2 ile bolumunden kalan 0  degil ise adim sayisi 1 artarak 3 ile capilip 1 eklenmis  hali gelecek
            }

        }
    }
    public static void cocktailSort(int[] arr) {
        cocktailSort2(arr, 0, arr.length - 1); // yazdigim methodu recursive cagirdim.
    }

    public static void cocktailSort2(int[] arr, int start, int end) {
        if (start >= end){ // start end degerinden buyukse program sona erecek.
            return;
        }

        boolean degisme = false; // sonrasında guncellemek ve degisebilir mi diye bakmak icin boolean tuttum.


        for (int i = start; i < end; i++) { // diziyi ileriye dogru bubble sort ile siraladim.
            if (arr[i] > arr[i + 1]) {
                int temp = arr[i];
                arr[i] = arr[i + 1];
                arr[i + 1] = temp;
                degisme = true;
            }
        }


        if (!degisme)
        {
            return; // eger degisme degiskeninde degisiklik olmadiysa dizi sıralidir anlamina gelir.
        }


        degisme = false; // bir sonraki siralamaya bakmak icin degiskeni tekrar false yaptim.
        for (int i = end - 1; i > start; i--) {
            if (arr[i] < arr[i - 1]) {
                int temp = arr[i];
                arr[i] = arr[i - 1];    // geriye yonlü bubble sort ile siraladim.
                arr[i - 1] = temp;
                degisme = true;
            }
        }


        cocktailSort2(arr, start + 1, end - 1); // methodu start ve end degerlerini guncelleyerek recursive cagirdim.
    }
}

