import java.util.Stack;

public class Sayac
{
    private String input;
    public Sayac(String input)
    {
        this.input = input;
    }
    public int say()
    {
        return countones(input,0); // yazdigim yardimci methodu burada cagirdim
    }
    public int countones(String input, int index)
    {
        int count = 0;
        if(index >= input.length())
        {
            return 0; // index out of bounds almamak icin base case yazdim.
        }
        else
        {
            if(input.charAt(index) == '1')
            {
                count = 1; // 1 gordugu zaman count degiskeni 1 olacak
            }
            else
            {
                count = 0; // 1 yoksa 0 olarak kalacak.
            }

        }
        int sonuc = countones(input,index+1); // her bir karakterde recursive cagirdim ve bunu yeni bir degiskene atadim.
        count += sonuc; // count degerini surekli guncelledim.
        return count;
    }
}
