import java.util.ArrayList;
import java.util.List;
class KarisikCalma implements CalmaStratejisi {
    @Override
    public void cal(List<Sarki> sarkiListesi) {
        List<Sarki> kopyaListe = new ArrayList<>(sarkiListesi);
        Random rand = new Random();
        for (int i = 0; i < kopyaListe.size(); i++) { //random sınıfıyla swap işlemi yapıyorum
            int randomIndex = rand.nextInt(kopyaListe.size());
            Sarki temp = kopyaListe.get(i);
            kopyaListe.set(i, kopyaListe.get(randomIndex));
            kopyaListe.set(randomIndex, temp);
        }
        for (Sarki sarki : kopyaListe) {
            sarki.oynat();
        }
    }
}

