import java.util.List;

public class KarisikCalma implements CalmaStratejisi{
    @Override
    public void cal(List<Sarki> sarkiListesi){
        int n = sarkiListesi.size();
        for (int i=n-1; i>0; i--){
            int rnd = (int)(Math.random()*(i+1)); //indeksi rastgele seçiyor
            Sarki temp = sarkiListesi.get(i);
            sarkiListesi.set(i, sarkiListesi.get(rnd)); //random gelen elemani koydum
            sarkiListesi.set(rnd, temp);
        }
        for (Sarki sarki: sarkiListesi){ //sarkiListesinin yeni hali
            sarki.oynat();
        }
    }
}

