import java.util.List;

public class KarisikCalma implements CalmaStratejisi {
    public void cal(List<Sarki> sarkiListesi) {
        boolean[] calindi = new boolean[sarkiListesi.size()];
        while(!bitti(calindi)) {
            int sayi = (int)(Math.random()*sarkiListesi.size());
            Sarki sarki = sarkiListesi.get(sayi);
            if(!calindi[sayi]) {
                sarki.oynat();
                calindi[sayi] = true;
            }
        }
    }
    public boolean bitti(boolean[] calindi) {
        for(boolean sarkiCalindi : calindi)
            if(!sarkiCalindi)
                return false;
        return true;
    }
}

