        }

        int count = 0;
        int index;

        while(count < sarkiListesi.size()){
            index = (int)(Math.random() * sarkiListesi.size());

            if(calindi[index] == false){
                calindi[index] = true;
                sarkiListesi.get(index).oynat();
                count++;
            }
        }

    }

}

