        if (str.length() == n * 2) {
            arr.add(str);
        } else {
            int close = 0;
            int open = n;
            for (int i = 0; i < str.length(); i++) {
                if (str.charAt(i) == '(') {
                    close++;
                    open--;
                } else {
                    close--;
                }
            }
            if (open > 0) {
                fun(str + "(", n, arr);
