        String x = "a1b2";
        String[] ar = method(x);
        for (int i = 0; i < ar.length; i++) {
            System.out.println(ar[i]);
        }
    }

    public static String[] method(String x) {
        x = x.toUpperCase();
        String[] arr = new String[2 ^ (x.length() / 2)];
        for (int i = 0; i < arr.length; i++) {
            arr[i] = x.substring(0, i) + x.substring(i).toLowerCase();
        }
        return arr;

    }

}

