import java.lang.module.FindException;
import java.net.BindException;

public class Q1 {
    public static void main(String[] args) {
        String str = "a1b2" ;
        System.out.println(kombinasyon(str, 0, str));
    }
    public static String kombinasyon(String s, int index, String modified){
        if (index == s.length()-1){
            return modified + olustur(s,index) ;
        }
        return kombinasyon(s,index + 1, modified + olustur(s, index) + boundControl(olustur(s, index),index));
    }

    private static String olustur(String s, int index){
        if ((s.charAt(index) >= 65) && (s.charAt(index) <= 90)){
            return " " + s.substring(1,index) + (char)(s.charAt(index)+32) + s.substring(index+1);
        }
        if ((s.charAt(index) >= 97) && (s.charAt(index) <= 122)){
            return " " + s.substring(0,index) + (char)(s.charAt(index)-32) + s.substring(index+1);
        }
        return "" ;
    }

    private static String boundControl(String s, int index){
        if (index < s.length()-1){
            return "" ;
        }
        return "" ;
    }
}

