public class Q1 {
    public static void main(String[] args) {
        String str = "a1b2";
        char[] harfler = new char[str.length()];
        int harfcount = 0;
        char[] diger = new char[str.length()];
        for(int x = 0; x < str.length(); x++) {
            int asci = str.charAt(x);
            if(asci >= 65 && asci <= 97+32) {
                harfler[x] = str.charAt(x);
                harfcount++;
            }    
            else    
                diger[x] = str.charAt(x);    
        }
        int dongucount = (int)Math.pow(2, harfcount);
        boolean a = false;
        for(int x = 0; x < dongucount; x++) {
            for(int y = 0; y < str.length(); y++) {
                if(harfler[y] >= 65 && harfler[y] <= 97+32){
                    if(x >= dongucount/2 && a == false) {
                        System.out.print((char)(harfler[y] - 32));
                        a = true;
                    }
                    else 
                        System.out.print(harfler[y]);
                }    
                else    
                    System.out.print(diger[y]);       
            }
            System.out.println(); 
            a = false;
        }
    }
}

