import java.util.ArrayList;
public class Q1 {

    public static void main(String[] args) {
        String str="a1b2";
        char[] ch = str.toCharArray();
        ArrayList<String> arrayList = new ArrayList<String>();
        arrayList.add(str);

        for(int i = 0;i<ch.length;i++){
            if (detectletter(ch[i])){
                String temp1 = str.substring(i, i+1);
                String temp2 = temp1.toUpperCase();
                if(temp1==temp2){
                    temp2=temp1.toLowerCase();
                }
                String newstr = str.substring(0, i) + temp2 + str.substring(i+1, str.length());
                arrayList.add(newstr);

            }
        }
        str = str.toUpperCase();
        char[] ch2 = str.toCharArray();
        arrayList.add(str);

        for(int i = 0;i<ch2.length;i++){
            if (detectletter(ch2[i])){
                String temp1 = str.substring(i, i+1);
                String temp2 = temp1.toUpperCase();
                if(temp1==temp2){
                    temp2=temp1.toLowerCase();
                }
                String newstr = str.substring(0, i) + temp2 + str.substring(i+1, str.length());
                arrayList.add(newstr);

            }
        }

        for(int i =0; i<arrayList.size()-1;i++){
            for (int j =i+1; j<arrayList.size();j++){
                    if(arrayList.get(i).equals(arrayList.get(j))){
                        arrayList.remove(j);
                    }

            }

        }
            System.out.println(arrayList);



    }

    public static boolean detectletter (char c){
        int i = (int) (c);
        if(i<80 && i>=65){
            return true;
        }
        else if(i<123 && i>=97){
            return true;
        }
        else{
            return false;
        }

    }

}

