    }

    public static void chars(String s){

        kombinasyon(s.toCharArray(),0); 
    }

    public static void kombinasyon(char[] chars, int index){
        if(index==chars.length){
            System.out.println(chars);

        }
        else if(chars[index] >= 65 && chars[index] <= 90) {
            chars[index] += 32;
            kombinasyon(chars, index + 1);
            chars[index] -= 32; 
            kombinasyon(chars, index + 1); 
        }
        else if(chars[index] >= 97 && chars[index] <= 122) {
            chars[index] -= 32;
            kombinasyon(chars, index + 1);
            chars[index] += 32; 
            kombinasyon(chars, index + 1); 
        }
        else{
            kombinasyon(chars, index+1);
        }
    }
}

