public class Question1 {
    //Q1 dedigimde compiler sorun cikardi caner hoca sebebnini bilmiyorum dedigi icin Question1 seklinde adlandirdim

    public static void main(String[] args) {
        String s = findPerm("abc" , 0); 
        System.out.println(s);
        String s1 = findPerm("ab" , 0);  
        System.out.println(s1);
        String s2 = findPerm("a12b" , 0);   
        System.out.println(s2);

    }

    public static String findPerm(String s , int i){
        char c = 'x';
        if(i >= s.length()) return "";

        if(s.charAt(i) != '0' && s.charAt(i) != '1' && s.charAt(i) != '2' && s.charAt(i) != '3' && s.charAt(i) != '4' && s.charAt(i) != '5' && s.charAt(i) != '6' && s.charAt(i) != '7' && s.charAt(i) != '8' && s.charAt(i) != '9' ){


            if(s.charAt(i) <= 'z' && s.charAt(i) >= 'a'){
                c = (char) (s.charAt(i) - 'A');
                return c + findPerm(s.substring(i+1) , i+1) ;
            }

            if(s.charAt(i) <= 'Z' && s.charAt(i) >= 'A'){
                c = (char) (s.charAt(i) + 32);
                return c + findPerm(s.substring(i+1) , i+1);
            }

        }

        else{
            return s.charAt(i) + findPerm(s.substring(i+1) , i+2) ;
        }   
        return "";
    }
}

