public class Q1 {
    public static void main(String[] args) {
        String s = "a1b2";
        print(s, "", 0);
    }

    public static void print(String s, String sub, int ind) {
        if (sub.length() == s.length()) {
            System.out.println(sub);
            return;
        }
        if (s.charAt(ind) <= '9' && s.charAt(ind) >= '0') {
            print(s, sub + s.charAt(ind), ind + 1);
        } else if (s.charAt(ind) <= 'z' && s.charAt(ind) >= 'a') {
            print(s, sub + s.charAt(ind), ind + 1);
            String r = sub + (char) (s.charAt(ind) - 32);
            print(s, r, ind + 1);
        } else if (s.charAt(ind) <= 'Z' && s.charAt(ind) >= 'A') {
            print(s, sub + s.charAt(ind), ind + 1);
            String r = sub + (char) (s.charAt(ind) + 32);
            print(s, r, ind + 1);
        }
    }
}

