        factory.printFactory();
        String s = "a1b2";
        char []ar = s.toCharArray();
        int index = 0;
        combinations(ar,index);


    }


    public static void combinations(char[] ar, int index) {

        if (index == ar.length) { // arrayin sonuna geldiğinde arrayi basması için base case oluşturdum.

            for (char c : ar) {
                System.out.print(c);
            }
            System.out.println();
            return;
        }


        if (Character.isLetter(ar[index]))
        {
            // Önce küçük harf olarak ayarla ve fonksiyonu bir sonraki karakter için çağır
            ar[index] = Character.toLowerCase(ar[index]);
            combinations(ar, index + 1);

            // Sonra büyük harf olarak ayarla ve fonksiyonu tekrar çağır
            ar[index] = Character.toUpperCase(ar[index]);
            combinations(ar, index + 1);
        }
        else
