import java.util.ArrayList;

public class bil211qui1z1{
    public static void main(String[] args) {
        String s = "a1b2";
        ArrayList<String> result=new ArrayList<>();
        kombinasyon(s, 0, "", result);
        System.out.println(result);
    }
   //büyük harf ile küçük harf arasında 32 fark olduğu belirtildi,o yüzden +-32 ile charları elde ettim
    public static void kombinasyon(String s, int index, String current, ArrayList<String> result) {

        if (index == s.length()) {
            result.add(current);
            return;
        }
        char c = s.charAt(index);


        if (c>='a'&&c<='z') {
            char d=(char)(c-32);
            kombinasyon(s, index + 1, current +c , result);
            kombinasyon(s, index + 1, current +d , result);
        }
        else if(c>='A'&&c<='Z'){
            char e=(char)(c+32);
            kombinasyon(s, index + 1, current +c , result);
            kombinasyon(s, index + 1, current +e , result);
        } 
        else {

            kombinasyon(s, index + 1, current + c, result);
        }
    }
}

