import java.util.ArrayList;

public class Quiz1Q1 {
    public static void main(String[] args){
        System.out.println(combination("a1b2").toString());
    }

    public static ArrayList combination(String s){

        int c = 0;

        for(int i = 0; i < s.length(); i++){
            if((s.charAt(i)>=65 && s.charAt(i)<91) || (s.charAt(i)>=97 && s.charAt(i)<123)){
                c += 1;
            }
        }

        ArrayList<String> a = new ArrayList<String>();

        boolean flag = true;
        while(flag){
            for(int i = 0; i < s.length(); i++){
                if(s.charAt(i)>=65 && s.charAt(i)<91){

                    if(!a.contains(s)){
                        a.add(s);
                    }
                    else{
                        s = s.substring(0, i+1) + s.substring(i+1,s.length()).toLowerCase();
                    }

                    s = s.substring(0, i) + (char)(s.charAt(i)+32) + s.substring(i+1, s.length());


                }

                else if(s.charAt(i)>=97 && s.charAt(i)<123){

                    if(!a.contains(s)){
                        a.add(s);
                    }

                    else{
                        s = s.substring(0, i+1) + s.substring(i+1,s.length()).toUpperCase();
                    }

                    s = s.substring(0, i) + (char)(s.charAt(i)-32) + s.substring(i+1, s.length());
                }
            }

            if(a.size()==Math.pow(2, c)){
                flag = false;
            }
        }

        return a;

    }
}

