package quiz;


import java.util.ArrayList; 
import java.util.List;

public class Kombinasyon {

    public static boolean isLetter(char ch) {  
        return (ch>= 'A' && ch<= 'Z') || (ch>= 'a' && ch<= 'z'); }    



    public static boolean Lower(char ch) 
    { return (ch>= 'a'&& ch<='z'); }

    public static boolean Upper(char ch) {

        return (ch>= 'A'&& ch<='Z'); }






    public static void Kombinasyon(char[] chars, int index, List<String> result) {

        if (index == chars.length) {  
            result.add(new String(chars)); return;   
        }  

        char currentChar = chars[index];


        if (isLetter(currentChar)) 
        {

            if (Lower(currentChar)) { 
                chars[index] = (char) (currentChar- 32);
                Kombinasyon(chars, index+1, result);

            }

            if (Upper(currentChar)) { chars[index] = (char) (currentChar+ 32);
                Kombinasyon(chars, index+1, result); 
            }
        }


        chars[index] = currentChar;
        Kombinasyon(chars, index+1, result);
    }

    public static void main(String[] args) {
         String s = "a1b2";
         List<String> result = new ArrayList<>();   
            Kombinasyon(s.toCharArray(), 0, result);
     System.out.println( result);
    }


}

