public class Q1{
    public static void combinations(String str, int index, String guncelStr){
        if(index == str.length()){ // stringin verilen ilk halini yazdıracak
            System.out.println(guncelStr);
            return;
        }
        char c = str.charAt(index);
        int ascii_degeri = (int)c;
        if ((ascii_degeri>=65 && ascii_degeri<=90) || (ascii_degeri>=97 && ascii_degeri<=122)) { //küçük harf mi veya büyük harf mi diye bakacak, rakam ise lower veya upper işlemi uygulamayacak.
            combinations(str, index+1, guncelStr+Character.toLowerCase(c));
            combinations(str, index+1, guncelStr+Character.toUpperCase(c));
        } 
        else 
            combinations(str, index+1, guncelStr+c);
    }
    public static void main(String[] args) {
        String str = "a1b2";
        combinations(str, 0, "");
    }
}

