package lab2;
import java.util.*;
public class lab2q1 {

    public static void main(String[] args) {
        String s = "a1b2";
        System.out.println(getAllPermutations(s));

    }

    public static void generatePermutations(String s, int index, List<String> result) {
        if (index == s.length()) {
            result.add(s);
            return;
        } 
        char currentChar = s.charAt(index);

        if (currentChar >= 'a' && currentChar <= 'z') { //harfse küçükse büyük büyükse küçük yapma // 32 yapma sebebim küçük a 97 büyük A 65 ascide
            generatePermutations(s.substring(0, index) + (char)(currentChar - 32) + s.substring(index + 1), index + 1, result);
        } 
        else if (currentChar >= 'A' && currentChar <= 'Z') {
            generatePermutations(s.substring(0, index) + (char)(currentChar + 32) + s.substring(index + 1), index + 1, result);
        }

        generatePermutations(s, index + 1, result); 
    }

    public static List<String> getAllPermutations(String s) {
        List<String> result = new ArrayList<>();
        generatePermutations(s, 0, result);
        return result;
    }
}

