public class Q1 {
    public static void recursive(String str, int index, String simdiki) {
        if (index == str.length()) {
            System.out.print(simdiki + " ");
            return;
        }


        char ch = str.charAt(index);


        // HARF İSE
        if (Character.isLetter(ch)) {
            recursive(str, index + 1, simdiki + Character.toLowerCase(ch));
            recursive(str, index + 1, simdiki + Character.toUpperCase(ch));
        } else {
            // RAKAM İSE
            recursive(str, index + 1, simdiki + ch);
        }
    }

    public static void main(String[] args) {
        String s = "a1b2";
        recursive(s, 0, "");
        System.out.println();
        String str = "Ab12cD";
        recursive(str,0,"");
    }
}

