import java.util.ArrayList;

public class Q1 {

    public static void main(String[] args) {
        System.out.println(subsets("a1b2"));
    }

    public static ArrayList<String> subsets(String input) {
        ArrayList<String> result = new ArrayList<>();
        recursive(input, 0, "", result);
        return result;
    }

    private static void recursive(String input, int index, String current, ArrayList<String> result) {
        if (index == input.length()) {
            result.add(current);
            return;
        }

        char ch = input.charAt(index);

        if (ch >= 'A' && ch <= 'Z') { 
            recursive(input, index + 1, current + ("" + ch).toLowerCase(), result);
            recursive(input, index + 1, current + ch, result);
        } 
        else if (ch >= 'a' && ch <= 'z') {
            recursive(input, index + 1, current + ("" + ch).toUpperCase(), result);
            recursive(input, index + 1, current + ch, result);
        } 
        else {
            recursive(input, index + 1, current + ch, result);
        }
    }
}

