public class CaseCombo {
    public static void main(String[] args){
        String s = "a1b2c3d4";
        caseCombo(s, 0);
    }

    public static void caseCombo(String s, int i){
        if (i == 0){
            System.out.println(s);
        }
        // string'i alalım. index'deki harf mi diye bakalım. harfse bir büyük yapalım bir küçük yapalım ve ekrana yazdıralım.
        // sonra index'i bir arttırarak metodu tekrar çağıralım. index == length olunca sadece yazdırsın.
        if (i == s.length() - 1){
            if ((int) s.charAt(i) >= 65 && s.charAt(i) <= 90){
                s = s.substring(0, i) + (char)(s.charAt(i) + 32) + s.substring(i + 1, s.length());
                System.out.println(s);
            }
            else if ((int) s.charAt(i) >= 97 && s.charAt(i) <= 122){
                s = s.substring(0, i) + (char)(s.charAt(i) - 32) + s.substring(i + 1, s.length());
                System.out.println(s);
            }
        }
        else {
            if ((int) s.charAt(i) >= 65 && s.charAt(i) <= 90){
                caseCombo(s, i + 1);
                s = s.substring(0, i) + (char)(s.charAt(i) + 32) + s.substring(i + 1, s.length());
                System.out.println(s);
                caseCombo(s, i + 1);
            }
            else if ((int) s.charAt(i) >= 97 && s.charAt(i) <= 122){
                caseCombo(s, i + 1);
                s = s.substring(0, i) + (char)(s.charAt(i) - 32) + s.substring(i + 1, s.length());
                System.out.println(s);
                caseCombo(s, i + 1);
            }
            else {
                caseCombo(s, i + 1);
            }
        }
        return;
    }
}

