    public static int uzunluk(String str) {
        String str1 = str.toUpperCase();
        int x = 0;
        for(int i = 0; i < str.length(); i++) {
            if(str1.charAt(i) >= 65 && str1.charAt(i) < 97) {
                x++;
            }
        }
        return x;
    }

    public static void sort(String str, int y) {
        String[] arr = new String[y];
        arr[0] = str;
        int t = 1;
        for(int i = 1; i < y+1; i++) {
            if(str.charAt(i-1) >= 65 && str.charAt(i-1) < 97) {
                String str2 = str.toLowerCase();
                String s1 = str.substring(0,i-1);
                String s2 = str2.substring(i-1,i);
                String s3 = str.substring(i);
                arr[t] = s1 + s2 + s3;
                t++;
            }
            else if(str.charAt(i-1) >= 97) {
                String str3 = str.toUpperCase();
                String s1 = str.substring(0,i-1);
                String s2 = str3.substring(i-1,i);
                String s3 = str.substring(i);
                arr[t] = s1 + s2 + s3;
                t++;
            }
        }

        for(int i = 0; i < y; i++) {
            System.out.print(arr[i] + " ");
        }
    }
}

