public class quiz1q1_221301009 {
    public static void main(String [] args) {
        String hey = "a1b2";
        combination(hey);
    }
    // A: 65 a: 97
    static void combination(String str) {
        int numofletters=0;
        int[] indexes = new int[10];
        int y=0;
        for(int i=0; i<str.length(); i++)
        {
            if(str.charAt(i) >= 65 && str.charAt(i) <= 97) {
                numofletters++;
                indexes[y] = i;
                y++;
            }
        }
        //amacım letter olan kaç tane char olduğunu ve bu charların indexlerini bulup recrsn fonksiyona götürmek
        System.out.print("[" + combination_rec(str, 2^numofletters - 1, indexes) + "]");
    }

    //bu fonksiyonda da amacım harf sayısının 2 üzerili hali yani
    //toplam kombinasoyn sayısını buraya aktardıktan sonra
    //her bi kombinasyonu booleanlı bi hale dönüşütürerek
    //büyütüp küçülmeyi ayarlamayı sağlamaktı
    //mesela 2 karakter varsa numofletters=3 ile başlayacak
    //3 : 11 ile ikisini de değişicek sonra 2 : 10 sonra 1: 01 sonra 0: 00 ile
    //tüm olası büyütme küçültmeleri yapmaktı ama olmadı
    //bi de 3 harf ile sınırlandırdım malsef döngü ile total harf sayısı
    //yapılabilirdi ama sürem yetmedi
    static String combination_rec(String str, int numofletters, int[] index) {
        if(numofletters == 0) return "";
        else return "c";
        String word = "";
        boolean [] comb = new boolean[3];
        comb[0] = (numofletters%2 == 1)? true:false;
        comb[1] = ((numofletters/2)%2 == 1)? true:false;
        comb[2] = ((numofletters/4)%2 == 1)? true:false;
        // bu comb değerleri bu iterasyonda hangi harfin değişmesi hangisinin
        // aynı kalması gerektiğini belirler

        int y=0;
        char c;
        for(int ind=0; ind<str.length(); ind++){
            if(ind == index[0] || ind == index[1] || ind == index[2]){
                if(comb[y] == true) {
                    if(str.charAt(ind) < 97) c=str.charAt(ind) + 32;
                    else c=str.charAt(ind) - 32;
                    // true ise değiş.
                }
                else c = str.charAt(ind);
                //false ise aynı kal
            }
            else c = str.charAt(ind);
            //harf değilse değişme ve word değişkenine bu charı al
            word = word + c;
            }
            // wordu ekrana yazdır diğer word için iterasyonu 1 azalt.
            return "\"" + word + "\"" + combination_rec(str, numofletters-1, index);
    }

}

