public class Q1_calismiyor {

	public static void main(String[] args) {

		String s = "a1b2";

		//abc
		/*
		 * abc
		 * abC
		 * aBc
		 * aBC
		 * Abc
		 * AbC
		 * ABc
		 * ABC
		 * 
		 * ab
		 * 
		 * Ab
		 * aB
		 * AB
		 */
//		int[] indexes = LetterIndexes(s);
//		int Nletters =0;	//Number of letters
//		for(int i : indexes) {
//			if(i != 0)
//				Nletters++;
//		}
//		
//		boolean[] changed = new boolean[s.length()];
//		changed = booleanReset(changed);
//		
//		for(int i=0; i<Math.pow(2, Nletters);i++) {
//		}
		System.out.println(recurv(s));

	}
	public static String recurv(String str) {
		if(str.length()==0)
			return "";
		char c = str.charAt(0);
		if( c >= 'A' && c <= 'Z')
			return ""+(char)(c+'a'-'A') + recurv(str.substring(1));
		else if(c >= 97 && c <= 122)
			return ""+(char)(c-('a'-'A')) + recurv(str.substring(1));
		return ""+c+recurv(str.substring(1));
	}



	public static int[] LetterIndexes(String str) {
		int[] indexes = new int[str.length()];

		for(int i =0; i<indexes.length; i++) {
			indexes[i] = 0;
		}

		for(int i=0; i<str.length();i++) {
			char c =str.charAt(i);
			if( c >= 65 && c <= 90)
				indexes[i] = 2;
			if(c >= 97 && c <= 122)
				indexes[i] = 1;
		}

		return indexes;
	}

	public static boolean[] booleanReset(boolean[] arr) {
		for(boolean a : arr) {
			a = false;
		}
		return arr;
	}

}

