    public static void printAll(String s,int index) {
        if(index+1==s.length())
        {
            System.exit(0);
        }
        else if(s.charAt(index)>64 & s.charAt(index)<91)
        {
            System.out.println(s.substring(0, index+1).toLowerCase() + s.substring(index+1));
        }
        else if(s.charAt(index)>96 & s.charAt(index)<123)
        {
            System.out.println(s.substring(0, index+1).toUpperCase() + s.substring(index+1));
        }
        else
            System.out.println(s);

        printAll(s,index+1);
    }
}



