                    }
                    System.out.print("Stok: ");
                    try {
                        stok = scanner.nextInt();
                    } catch (Exception e) {
                        throw new OzellikHatasiException("HATA: Stok miktarinda hatali girdi.");
                    }
                    try {
                        esyaEkleme(isim, stok);
                    } catch (StokNegatifException e) {
                        System.out.println(e.getMessage());
                    }
                }
                else if(secim == 2) {
                    System.out.print("Eşya ismi: ");
                    isim = scanner.next();
                    esyaCikarma(isim);
                }
                else if(secim == 2) {
                    System.out.print("Eşya ismi: ");
                    isim = scanner.next();
                    System.out.print("Stok: ");
                    stok = scanner.nextInt();
                }
                else if(secim == 3) {    
                    stokGuncelleme(isim, stok);
                }
                else if(secim == 4) {
                    envanterListeleme();
                }
                else if(secim == 5) {
                    dosyayaKaydetme();
                }
                else if(secim == 6) {
                    dosyadanGeriYukleme();
                }
                else if(secim == 7) {
                    cikis();
                }
                else    
                    System.out.println("Geçersiz seçim!"); 
            }                     
            System.out.println("~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~");
        }
        scanner.close();
    }

    public void esyaEkleme(String isim, int stok) throws OzellikHatasiException, StokNegatifException {
        if (stok < 0) {
            throw new OzellikHatasiException("HATA: Stok miktari negatif olamaz.");
        }
        else {
            int count = 0;
            for (int x = 0; x < envanter.size(); x++) {
                if (envanter.get(x).getIsim().equals(isim)) {
                    envanter.get(x).stokGuncelle(stok);
                    count++;
                    System.out.println(envanter.get(x).getIsim() + " ürününün stoğu " + stok + " olarak güncellendi.");
                }
            }    
            if(count == 0) {
                envanter.add(new EnvanterKaydi(isim, stok));
                System.out.println(isim + " ürünü envantere eklendi.");
            }
        }   
    }

    public void esyaCikarma(String isim) throws EsyaYokException {
        boolean d = false;
        for (int x = 0; x < envanter.size(); x++) {
            if (envanter.get(x).getIsim().equals(isim)) {
                envanter.remove(x);
                d = true;
                System.out.println(isim + " ürünü envanterden çikarildi.");
            }
        }
        if(d == false) {
            throw new EsyaYokException(isim + " ürünü envanterde bulunamadi.");
        }
    }

    public void stokGuncelleme(String isim, int stok) throws StokNegatifException, EsyaYokException {
        boolean d = false;
        for (int x = 0; x < envanter.size(); x++) {
            if (envanter.get(x).getIsim().equals(isim)) {
                envanter.get(x).stokGuncelle(stok);
                d = true;
                System.out.println(envanter.get(x).getIsim() + " ürününün stoğu " + stok + " olarak güncellendi.");
            }
        }
        if(d == false) {
            throw new EsyaYokException(isim + " ürünü envanterde bulunamadi.");
        }
    }

    public void envanterListeleme() {
        int count = 0;
        for (int x = 0; x < envanter.size(); x++) {
            if(x == 0){
                System.out.println("Envanter Listesi:");
            }
            System.out.println("Ürün İsmi: " + envanter.get(x).getIsim() + ", Ürün Stoğu: " + envanter.get(x).getStok());
            count++;
        }
        if(count == 0)
            System.out.println("Envanterde ürün yok!");
    }

    public void dosyayaKaydetme() throws IOException {
        File file = new File("envanter.txt");
        FileWriter writer = new FileWriter(file);
        for (int x = 0; x < envanter.size(); x++) {
            writer.write(envanter.get(x).getIsim() + " " + envanter.get(x).getStok() + "\n");
        }
        System.out.println("Envanter 'envanter.txt' adli dosyaya kaydedildi.");
