    }

    public void dosyadanGeriYukleme() throws DosyaBulunamadiException , BosDosyaException, NumberFormatException, StokNegatifException, OzellikHatasiException{
        try(BufferedReader reader = new BufferedReader(new FileReader("envanter.txt"))) {
