public class EnvanterSistemi {
    private static final String DOSYA_ADI = "envanter.txt";
    private static Map<String, EnvanterKaydi> envanter = new HashMap<>();

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            System.out.println("1. Eşya Ekleme\n2. Eşya Çıkarma\n3. Stok Güncelleme\n4. Envanter Listeleme\n5. Dosyaya Kaydetme\n6. Dosyadan Geri Yükleme\n7. Çıkış");
            int secim = scanner.nextInt();
            scanner.nextLine();

            switch (secim) {
                case 1:
                    esyaEkle(scanner);
                    break;
                case 2:
                    esyaCikarma(scanner);
                    break;
                case 3:
                    stokGuncelle(scanner);
                    break;
                case 4:
                    envanterListele();
                    break;
                case 5:
                    dosyayaKaydet();
                    break;
                case 6:
                    dosyadanGeriYukle();
                    break;
                case 7:
                    System.exit(0);
            }
        }
    }

    private static void esyaEkle(Scanner scanner) {
        System.out.print("Eşya adı: ");
        String isim = scanner.nextLine();
        System.out.print("Başlangıç stoğu: ");
        int stok = scanner.nextInt();
        scanner.nextLine();

        envanter.put(isim, new EnvanterKaydi(isim, stok));
        System.out.println(isim + " envantere eklendi. Güncel stok: " + stok);
    }

    private static void esyaCikarma(Scanner scanner) {
        System.out.print("Silinecek eşya adı: ");
        String isim = scanner.nextLine();

        if (!envanter.containsKey(isim)) {
            System.err.println("HATA: İstenen eşya envanterde mevcut değil.");
            return;
        }
        envanter.remove(isim);
        System.out.println(isim + " envanterden silindi.");
    }

    private static void stokGuncelle(Scanner scanner) {
        System.out.print("Eşya adı: ");
        String isim = scanner.nextLine();
        if (!envanter.containsKey(isim)) {
            System.err.println("HATA: İstenen eşya envanterde mevcut değil.");
            return;
        }
        System.out.print("Stok değişimi: ");
        int miktar = scanner.nextInt();
        scanner.nextLine();

        try {
            envanter.get(isim).stokGuncelle(miktar);
            System.out.println(isim + " güncellendi. Yeni stok: " + envanter.get(isim).getStok());
        } catch (StokNegatifException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void envanterListele() {
        if (envanter.isEmpty()) {
            System.out.println("Envanterde kayıtlı eşya bulunmamaktadır.");
            return;
        }
        for (EnvanterKaydi kayit : envanter.values()) {
            System.out.println(kayit.getIsim() + " - Stok: " + kayit.getStok());
        }
    }

    private static void dosyayaKaydet() {
        try (PrintWriter writer = new PrintWriter(new FileWriter(DOSYA_ADI))) {
            for (EnvanterKaydi kayit : envanter.values()) {
                writer.println(kayit.getIsim() + "," + kayit.getStok());
            }
        } catch (IOException e) {
            System.err.println("HATA: Dosya yazma hatası.");
        }
    }

    private static void dosyadanGeriYukle() {
        try (BufferedReader reader = new BufferedReader(new FileReader(DOSYA_ADI))) {
            envanter.clear();
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                if (parts.length < 2) throw new BosDosyaException();
                envanter.put(parts[0], new EnvanterKaydi(parts[0], Integer.parseInt(parts[1])));
            }
        } catch (IOException | BosDosyaException e) {
            System.err.println(e.getMessage());
        }
    }
}

