import java.util.ArrayList;
import java.util.Scanner;

public class KelimeSayaci {
    public static void main(String[] args) {
        okuyucu("metin.txt");
        kelimeSayac("metin.txt", "kelimeler.txt");
    }

    public static String okuyucu(String fileName) {
        String str = "";
        Scanner scan = null;
        String removed = "";
        try {
            scan = new Scanner(new FileInputStream(fileName));

            if (!scan.hasNextLine()) {
                throw new BosDosyaException();
            }
            while (scan.hasNextLine()) {

                str = scan.nextLine();

            }
            scan.close();

            removed = str.replaceAll("\\p{Punct}", "");
            return removed;
        } catch (FileNotFoundException e) {
            try {
                throw new DosyaBulunamadiException();
            } catch (DosyaBulunamadiException ex) {
                ex.getMessage();
            }

        } catch (BosDosyaException e) {
            e.getMessage();

        }
        return null;

    }

    public static ArrayList<String> okuyucu2(String fileName) {
        ArrayList<String> removed = new ArrayList<>();
        Scanner scan = null;
        removed.
        try {
            scan = new Scanner(new FileInputStream(fileName));
            if (!scan.hasNextLine()) {
                throw new BosDosyaException();

            }
            while (scan.hasNextLine()) {

                removed.add(scan.nextLine());

            }

            scan.close();
            return removed;

        } catch (FileNotFoundException e) {
            try {
                throw new DosyaBulunamadiException();
            } catch (DosyaBulunamadiException ex) {
                ex.getMessage();
            }

        } catch (BosDosyaException e) {
            e.getMessage();

        }
        return null;

    }

    public void kelimeler() {
        PrintWriter kelime = null;
        try {
            kelime = new PrintWriter(new FileOutputStream("sonuc.txt"));
        } catch (FileNotFoundException e) {
            try {
                throw new DosyaBulunamadiException();
            } catch (DosyaBulunamadiException ex) {
                ex.getMessage();
            }
        }
    }

    public static void kelimeSayac(String file1, String file2) {
        if (file1 == null || file2 == null) {
            return;
        }
        String str1 = okuyucu(file1);
        ArrayList<String> str2 = okuyucu2(file2);

        int counts[] = new int[str2.size()];
        int counter = 0;
        for (int i = 0; i < str2.size(); i++) {
            int l = str2.get(i).length();
            counts[i] = counter;
            counter = 0;
            for (int j = 0; j < str1.length(); j++) {

                if (str2.get(i).equals(str1.substring(j, l))) {
                    counter++;
                }

            }

        }
        int total = 0;
        for (int i = 0; i < counts.length; i++) {
            total += counts[i];
        }
        try {
            if (total == 0)
                throw new KelimeBulunamadiException();
        } catch (KelimeBulunamadiException e) {
            e.getMessage();
        }
    }
}

