        ArrayList<EnvanterKaydi> esyalar = new ArrayList<>();
        boolean a = true;


        try{ 
            while(a == true){
                int islem = scan.nextInt();

                if(islem == 1){ // esya ekleme
                    esyaEkle(esyalar, "buzdolabı", 5);
                }

                else if(islem == 2){ // esya cıkarma
                    esyaCikar(esyalar, "buzdolabı");
                }

                else if(islem == 3){ // stok guncelleme
                    stokGuncelle(esyalar, "buzdolabı", 6);
                }

                else if(islem == 4){ // envanter listeleme
                    for(EnvanterKaydi e : esyalar){
                        System.out.println("isim: " + e.getIsim() + " stok: " + e.getStok());
                    }
                }

                else if(islem == 5){ // dosyaya kaydetme
                    try{
                        ObjectOutputStream oos = new ObjectOutputStream(new FileOutputStream("sabit.txt", true));
                        for(EnvanterKaydi e : esyalar){
                            oos.writeObject(e);
                        }

                        oos.close();
                    }
                    catch(IOException e){ 
                        System.out.println(e.getMessage());
                    }
                }
                else if(islem == 6){ // dosyadan geri yükleme
                    try{
                        File file = new File("sabit.txt");
                        if(file.exists() == false){
                            throw new DosyaBulunamadiException();
                        }

                        ObjectInputStream ois = new ObjectInputStream(new FileInputStream("sabit.txt"));
                        ArrayList<EnvanterKaydi> ek = new ArrayList<>();
                        for(EnvanterKaydi e : ek){ 
                            Object o = ois.readObject(); 
                            ek.add((EnvanterKaydi) o);
                        }  

                        ois.close();;
                    }
                    catch(IOException e ){
                        System.out.println(e.getMessage());
                    }

                }
                else if( islem == 7){ // çıkış
                    a = false;
                }

            }
        }
        catch(DosyaBulunamadiException e){
            System.out.println(e.getMessage());
        }
        catch(ClassNotFoundException e){
            System.out.println(e.getMessage());
        }







    }

    public static ArrayList<EnvanterKaydi> esyaEkle(ArrayList<EnvanterKaydi> arr, String esyaIsmi, int miktar){
