        try{

            if(esyaIsmi == null){
                throw new OzellikHatasiException();
            }
            for(EnvanterKaydi e : arr){
                if(e.getIsim().equals(esyaIsmi)){
                        esyaZatenVarMi = true;
                        e.StokGuncelle(e.getStok());

                }
            }
            if(esyaZatenVarMi == false){
                arr.add(new EnvanterKaydi(esyaIsmi, miktar));
            }        
        }
        catch(OzellikHatasiException e){
            System.out.println(e.getMessage());
        }

        return arr;
    }

    public static ArrayList<EnvanterKaydi> esyaCikar(ArrayList<EnvanterKaydi> arr, String esyaIsmi){
        boolean esyaZatenVarMi = false;
        try{
            for(EnvanterKaydi e : arr){
                if(esyaIsmi.equals(e.getIsim())){
                    esyaZatenVarMi = true;
                    arr.remove(e);
                }
            }

            if(esyaZatenVarMi == false){
                throw new EsyaYokException();
            }

        }
        catch(EsyaYokException e){
            System.out.println(e.getMessage());
        }

        return arr;
    }

    public static ArrayList<EnvanterKaydi> stokGuncelle(ArrayList<EnvanterKaydi> arr, String esyaIsmi, int miktar ){
