            throw new StokNegatifException(); 
        }

        stok+=miktar;
    }

    public void setIsim(String isim) {
        this.isim = isim;
    }

    public void setStok(int stok) {
        this.stok = stok;
    }

    public String getIsim() {
        return isim;
    }

    public int getStok() {
        return stok;
    }


}

