import java.io.*;
import java.util.ArrayList;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        File bin = new File("kayit.bin");
        ArrayList<EnvanterKaydi> esyalar = new ArrayList<>();
        islem(esyalar,bin);
    }
    public static void islem(ArrayList<EnvanterKaydi> esyalar, File bin) {
        System.out.println();
        System.out.println("Yapmak istediğiniz işlemi seçin:");
        System.out.println("Eşya Ekleme (1)");
        System.out.println("Eşya Çıkarma (2)");
        System.out.println("Stok Güncelleme (3)");
        System.out.println("Envanter Listeleme (4)");
        System.out.println("Dosyaya Kaydetme (5)");
        System.out.println("Dosyadan Geri Yükleme (6)");
        System.out.println("Çıkış (0)");

        Scanner in = new Scanner(System.in);
        int secim = in.nextInt();

        if(secim == 1)
            ekle(esyalar,bin);
        else if(secim == 2)
            cikar(esyalar, bin);
        else if(secim == 3)
            guncelle(esyalar, bin);
        else if(secim == 4)
            listele(esyalar, bin);
        else if(secim == 5)
            kaydet(esyalar, bin);
        else if(secim == 6)
            yukle(esyalar, bin);
        else if(secim == 0) {
            System.out.println("Programdan çıkılıyor...");
            System.exit(0);
        }

        else {
            System.out.println("Yanlış sayı girdiniz, tekrar deneyin.");
            islem(esyalar,bin);
        }
    }
    public static void ekle(ArrayList<EnvanterKaydi> esyalar,File bin) {
        Scanner in = new Scanner(System.in);
        System.out.print("Yeni eşya ismi girin:");
        String isim = in.nextLine().toUpperCase();

        EnvanterKaydi kopyaEsya = null;
        boolean unique = true;
        for(EnvanterKaydi esya : esyalar)
            if (isim.equals(esya.getIsim())) {
                unique = false;
                kopyaEsya = esya;
                System.out.println("Bu eşya zaten bulunuyor, yeni eşya oluşturulmadı.");
                break;
            }
        if(unique)
            try {
                System.out.print("Yeni eşya stok miktarını girin:");
                int stok = in.nextInt();

                EnvanterKaydi esya = new EnvanterKaydi(isim,stok);
                esyalar.add(esya);
                System.out.println("Eşya kaydı başarıyla tamamlandı.");
            } catch (StokNegatifException e) {
                System.out.println(e.getMessage());
            } finally {
                islem(esyalar, bin);
            }
        else
            try {
                System.out.print("Yeni stok miktarını girin: (Şu anki stok miktarı: "+kopyaEsya.getStok()+")");
                int stok = in.nextInt();
                kopyaEsya.stokGuncelle(stok);
            } catch (StokNegatifException e) {
                System.out.println(e.getMessage());
                islem(esyalar, bin);
            } finally {
                islem(esyalar, bin);
            }
    }
    public static void cikar(ArrayList<EnvanterKaydi> esyalar,File bin) {
        Scanner in = new Scanner(System.in);
        System.out.println("Çıkarmak istediğiniz eşyanın ismini girin:");
        String isim = in.nextLine();

        boolean exists = false;
        EnvanterKaydi silinecekEsya = null;
        for(EnvanterKaydi esya : esyalar)
            if(isim.equals(esya.getIsim())) {
                exists = true;
                silinecekEsya = esya;
                break;
            }
        try {
            if(exists)
                esyalar.remove(silinecekEsya);
            else
                throw new EsyaYokException();
        } catch (EsyaYokException e) {
            System.out.println(e.getMessage());
        } finally {
            islem(esyalar, bin);
        }
    }
    public static void guncelle(ArrayList<EnvanterKaydi> esyalar,File bin) {
        Scanner in = new Scanner(System.in);
        System.out.println("Stoğunu güncellemek istediğiniz eşyanın ismini girin:");
        String isim = in.nextLine();

        boolean exists = false;
        EnvanterKaydi guncellenecekEsya = null;
        for(EnvanterKaydi esya : esyalar)
            if(isim.equals(esya.getIsim())) {
                exists = true;
                guncellenecekEsya = esya;
                break;
            }
        try {
            if(exists) {
                System.out.println("Yeni stok miktarını girin: (Şu anki stok miktarı: "+guncellenecekEsya.getStok()+")");
                int stok = in.nextInt();
                guncellenecekEsya.stokGuncelle(stok);
            }
            else
                throw new EsyaYokException();
        } catch (EsyaYokException e) {
            System.out.println(e.getMessage());
        } catch (StokNegatifException e) {
            System.out.println(e.getMessage());
        } finally {
            islem(esyalar, bin);
        }
    }
    public static void listele(ArrayList<EnvanterKaydi> esyalar,File bin) {
        for(EnvanterKaydi esya : esyalar) {
            System.out.println("Eşya ismi: "+esya.getIsim());
            System.out.println("Eşya stoğu: "+esya.getStok());
            System.out.println();
        }
        islem(esyalar, bin);
    }
    public static void kaydet(ArrayList<EnvanterKaydi> esyalar,File bin) {
        try {
            try(ObjectOutputStream out = new ObjectOutputStream(new FileOutputStream(bin))) {
                for(EnvanterKaydi esya : esyalar)
                    out.writeObject(esya);
            } catch(FileNotFoundException e) {
                throw new DosyaBulunamadiException();
            } catch (IOException e) {
                System.out.println("Dosyaya yazılırken beklenmedik bir hata oluştu.");
            }
        } catch (DosyaBulunamadiException e) {
            System.out.println(e.getMessage());
        } finally {
            islem(esyalar, bin);
        }
    }
    public static void yukle(ArrayList<EnvanterKaydi> esyalar,File bin) {
        try {
            try(ObjectInputStream in = new ObjectInputStream(new FileInputStream(bin))) {
               boolean isEmpty = true;
                for(EnvanterKaydi esya : esyalar) {
                   EnvanterKaydi yazilacakEsya = (EnvanterKaydi) in.readObject();
                   if(yazilacakEsya == null) {
                       if(isEmpty)
                           throw new BosDosyaException();
                       break;
                   }
                   isEmpty = false;
                   esya.setIsim(yazilacakEsya.getIsim());
                   esya.setStok(yazilacakEsya.getStok());
                   System.out.println("Dosya başarılı bir şekilde yüklendi.");
               }
            } catch(FileNotFoundException e) {
                throw new DosyaBulunamadiException();
            } catch (BosDosyaException e) {
                System.out.println(e.getMessage());
            } catch (IOException | ClassNotFoundException e) {
                System.out.println("Dosya okunurken beklenmedik bir hata oluştu.");
            }
        } catch (DosyaBulunamadiException e) {
            System.out.println(e.getMessage());
        } finally {
            islem(esyalar, bin);
        }
    }
}

