        env.add(new EnvanterKaydi(isim, stok));
    }
    public static void esyaCikar() throws EsyaYokException{
        Scanner keyboard = new Scanner(System.in);
        System.out.print("Silinecek Eşya Adı: ");
        String isim = keyboard.nextLine();
        for(int i = 0; i< env.size() ; i++){
            if (env.get(i).getIsim().equals(isim)){
                env.remove(i);
                return;
            }
        }
        throw new EsyaYokException();
    }
    public static void stokGuncelle() throws EsyaYokException{
        Scanner keyboard = new Scanner(System.in);
        System.out.print("Eşya Adı: ");
        String isim = keyboard.nextLine();
        for(EnvanterKaydi kayit : env){
            if(kayit.getIsim().equals(isim)){
                System.out.print("Stok Değişim Miktarı (+/-): ");
                int miktar = keyboard.nextInt();
                keyboard.nextLine();
                try{
                    kayit.stokGuncelle(miktar);
                }catch(StokNegatifException e){
                    System.err.println(e.getMessage());
                }
                return;
            }
        }
        throw new EsyaYokException();
    }
    public static void envanteriListele(){
        if(env.isEmpty()){
            System.out.println("Envanter boş.");
            return;
        }
        for(EnvanterKaydi k: env){
            System.out.println(k.getIsim() + " - Stok: " + k.getStok());
        }
    }
    public static void dosyayaKaydet(){
        try (PrintWriter writer = new PrintWriter(new FileWriter(dosyaAdi))){
            for (EnvanterKaydi k: env){
                writer.println(k.getIsim() + "," + k.getStok());
            }
        } catch(IOException e){
            System.err.println("Dosya yazma hatası!");
        }
    }
    public static void dosyadanYukle(){
        try (BufferedReader rd = new BufferedReader(new FileReader(dosyaAdi))){
            String cols;
            env.clear();
            while((cols = rd.readLine()) != null){
                String[] elemanlar = cols.split(",");
                if (elemanlar.length != 2) 
                    continue;
                env.add(new EnvanterKaydi(elemanlar[0], Integer.parseInt(elemanlar[1])));
            }
            if(env.isEmpty()){
                throw new BosDosyaException();
            }
        } catch(FileNotFoundException e){
            System.err.println(new DosyaBulunamadiException().getMessage());
        } catch(IOException | BosDosyaException e){
            System.err.println(e.getMessage());
        }
    }
}

