import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.InputMismatchException;
import java.util.Scanner;

public class Quiz3Q1 extends Exception{


    public static ArrayList<EnvanterKaydi> esyalar = new ArrayList<>();

    public static void main(String[] args){

        while(true){

            System.out.println("-----Terminal Menüsü-----");
            System.out.println("1. Eşya Ekleme\n2. Eşya Çıkarma\n3. Stok Güncelleme\n4. Envanter Listeleme\n5. Dosyaya Kaydetme\n6. Dosyadan Geri Yükleme\n7. Çıkış");
            Scanner kullanici = new Scanner(System.in);

            try{
                int karar = kullanici.nextInt();

                if(karar==1){
                    System.out.println("Eklemek istediğiniz eşyayı giriniz: ");
                    String esya = kullanici.next();
                    kullanici.nextLine();
                    System.out.println("Bu eşyadan eklemek istediğiniz stok miktarını giriniz: ");
                    int miktar = kullanici.nextInt();

                    try{
                        EsyaEkle(esya, miktar);
                    }

                    catch(StokNegatifException e){
                        System.out.println(e.getMessage());
                    }
                    catch(OzellikHatasiException e){
                        System.out.println(e.getMessage());
                    }

                }

                else if(karar==2){

                    System.out.println("Çıkarmak istediğiniz eşyayı giriniz: ");
                    String esya = kullanici.next();
                    kullanici.nextLine();
                    try{
                        EsyaCikar(esya);
                    }
                    catch(EsyaYokException e){
                        System.out.println(e.getMessage());
                    }


                }
                else if(karar==3){
                    System.out.println("Güncellemek istediğiniz eşyayı giriniz: ");
                    String esya = kullanici.next();
                    kullanici.nextLine();
                    System.out.println("Güncellemek istediğiniz miktarı giriniz: ");
                    int miktar = kullanici.nextInt();
                    try{
                        StokGuncelle(esya, miktar);
                    }
                    catch(StokNegatifException e){
                        System.out.println(e.getMessage());
                    }
                    catch(EsyaYokException e){
                        System.out.println(e.getMessage());
                    }

                }

                else if(karar==4){
                    EnvanteriListele();
                }
                else if(karar==5){
                    try{
                        DosyayaKaydet();
                    }
                    catch(DosyaBulunamadiException e){
                        System.out.println(e.getMessage());
                    }

                }
                else if(karar==6){

                    try{
                        DosyadanYukle();
                    }

                    catch(DosyaBulunamadiException e){
                        System.out.println(e.getMessage());
                    }
                    catch(BosDosyaException e){
                        System.out.println(e.getMessage());
                    }

                }

                else if(karar==7){
                    Cikis();
                }

            }
            catch(InputMismatchException e){
                System.out.println("Lütfen bir sayı giriniz!");
            }
        }



    }

    public static void EsyaEkle(String esya, int miktar) throws StokNegatifException, OzellikHatasiException{

        if(miktar<0){
            throw new StokNegatifException("HATA: Stok miktarı negatif olamaz.");
        }

        boolean flag = false;

        for(int i = 0; i < esyalar.size(); i++){
            if(esyalar.get(i).getIsim().equals(esya)){
                flag = true;
            }
        }

        if(flag){
            throw new OzellikHatasiException("HATA: Eklemeye çalıştığınız eşya zaten envanterde mevcut.");
        }


        EnvanterKaydi e = new EnvanterKaydi(esya, miktar);
        esyalar.add(e);


        System.out.println(miktar + " tane " + esya + " başarıyla eklendi!");
    }

    public static void EsyaCikar(String esya) throws EsyaYokException{

        boolean flag = false;

        for(int i = 0; i < esyalar.size(); i++){
            if(esyalar.get(i).getIsim().equals(esya)){
                flag = true;
            }
        }

        if(!flag){
            throw new EsyaYokException("HATA: istenen eşya envanterde mevcut değil.");
        }



        int index = 0;

        for(int i = 0; i < esyalar.size(); i++){
            if(esyalar.get(i).getIsim()==esya){
                index = i;
            }
        }

        esyalar.remove(index);


        System.out.println(esya + " başarıyla envanterden silindi.");

    }

    public static void StokGuncelle(String esya, int miktar) throws StokNegatifException, EsyaYokException{
        if(miktar<0){
            throw new StokNegatifException("HATA: Stok miktarı negatif olamaz.");
        }

        boolean flag = false;

        for(int i = 0; i < esyalar.size(); i++){
            if((esyalar.get(i).getIsim().equals(esya))){
                flag = true;
            }
        }

        if(!flag){
            throw new EsyaYokException("HATA: istenen eşya envanterde mevcut değil.");
        }

        int index = 0;

        for(int i = 0; i < esyalar.size(); i++){
            if(esyalar.get(i).getIsim()==esya){
                index = i;
            }
        }

        esyalar.get(index).stokGuncelle(miktar);

        System.out.println(esya + " eşyasının sayısı başarıyla " + miktar + " olarak güncellendi.");

    }

    public static void EnvanteriListele(){

        if(esyalar.isEmpty()){
            System.out.println("Envanterde eşya yok.");
        }

        for(int i = 0; i < esyalar.size(); i++){
            System.out.println((i+1) + ". " + esyalar.get(i));
        }
    }

    public static void DosyayaKaydet() throws DosyaBulunamadiException{

        try{
            FileOutputStream k = new FileOutputStream("envanter.txt");
            PrintWriter pw = new PrintWriter(k);

            for(int i = 0; i < esyalar.size(); i++){
                pw.println(esyalar.get(i));
                System.out.println(i);
            }

            pw.flush();
            pw.close();

            System.out.println("Veriler envanter.txt'ye başarıyla kaydedildi.");

        }
        catch(FileNotFoundException e){
            throw new DosyaBulunamadiException("HATA: envanter dosyası bulunamadı.");
        }



    }

    public static void DosyadanYukle() throws DosyaBulunamadiException, BosDosyaException{

        esyalar.clear();


        try{
            FileInputStream envanter = new FileInputStream("envanter.txt");
            Scanner yukleme = new Scanner(envanter);

            if(!yukleme.hasNext()){
                throw new BosDosyaException("HATA: envanter dosyası boş.");
            }

            while(yukleme.hasNext()){
                String esya = yukleme.next();
                int miktar = yukleme.nextInt();

                EnvanterKaydi e = new EnvanterKaydi(esya, miktar);

                esyalar.add(e);

            }

            System.out.println("Veriler envanter.txt'den başarıyla yüklendi.");


        }
        catch (FileNotFoundException e){
            throw new DosyaBulunamadiException("HATA: envanter dosyası bulunamadı.");
        }

    }

    public static void Cikis(){
        System.exit(0);
    }


}

