import java.io.*;
import java.util.ArrayList;
import java.util.Scanner;

public class Main {

    public static void main(String[] args) throws InputException, Exception {
        Scanner scan = new Scanner(System.in);
        ArrayList<EnvanterKaydi> array = new ArrayList<>();

        int input;
        String isim;
        int stok;

        System.out.println("\nGirdi Seçenekleri:");
        System.out.println("1 - Eşya Ekleme");
        System.out.println("2 - Eşya Çıkarma");
        System.out.println("3 - Stok Güncelleme");
        System.out.println("4 - Envanter Listeleme");
        System.out.println("5 - Dosyaya Kaydetme");
        System.out.println("6 - Dosyadan Okuma");
        System.out.println("7 - Çıkış");

        while (true) {

            System.out.print("\nİşlem Girdisi: ");

            input = scan.nextInt();
            scan.nextLine(); // Tampon temizleme

            switch (input) {
                case 1: // Kayıt ekle veya stok güncelle
                    try {
                        System.out.print("\nEşya adı: ");
                        isim = scan.nextLine();
                        System.out.print("Stok miktarı: ");
                        stok = scan.nextInt();
                        envanterEkleme(array, isim, stok);
                    } catch (Exception e) {
                        throw new OzellikHatasiException();
                    }
                    break;

                case 2: // Kayıt sil
                    System.out.print("\nSilinecek eşyanın adı: ");
                    isim = scan.nextLine();
                    boolean esyaMevcut = false;
                    for (EnvanterKaydi kayit : array) {
                        if (kayit.getIsim().equals(isim)) {
                            array.remove(kayit);
                            esyaMevcut = true;
                            break;
                        }
                    }
                    if (!esyaMevcut) {
                       throw new EsyaYokException();
                    } 
                    System.out.println("Eşya silindi.");
                    break;

                case 3: // Stok güncelle
                    System.out.print("\nStok güncellenecek eşyanın adı: ");
                    isim = scan.nextLine();
                    System.out.print("Stok değişimi: ");
                    stok = scan.nextInt();

                    for (EnvanterKaydi kayit : array) {
                        if (kayit.getIsim().equals(isim)) {
                            try {
                                kayit.stokGuncelle(stok);
                                break;
                            } catch (StokNegatifException e) {
                                throw new StokNegatifException();
                            }
                        }
                    }
                    break;

                case 4: // Kayıtları listele
                    System.out.println("\nMevcut Envanter:");
                    for (EnvanterKaydi kayit : array) {
                        System.out.println(kayit);
                    }
                    break;

                case 5: // Dosyaya kaydet
                    try (BufferedWriter writer = new BufferedWriter(new FileWriter("/Users/erenyilmaz/Desktop/TOBB/Bil211/New/Labs/Lab6/envanter.txt"))) {
                        for (EnvanterKaydi kayit : array) {
                            writer.write(kayit.getIsim() + " " + kayit.getStok());
                            writer.newLine();
                        }
                    } catch (IOException e) {
                        System.err.println("HATA: envanter.txt dosyası oluşturulamadı.");
                        System.exit(1);
                    }
                    break;

                case 6: // Dosyadan oku
                    try (BufferedReader metinOkuyucu = new BufferedReader(new FileReader("/Users/erenyilmaz/Desktop/TOBB/Bil211/New/Labs/Lab6/envanter.txt"))) {

                        String line;
                        boolean metinDosyasiBos = true;

                        while ((line = metinOkuyucu.readLine()) != null) {
                            if (!line.isEmpty()) {
                                metinDosyasiBos = false;
                                isim = line.split(" ")[0];
                                stok = Integer.parseInt(line.split(" ")[1]);
                                envanterEkleme(array, isim, stok);
                            }
                        }

                        if (metinDosyasiBos) {
                            throw new BosDosyaException();
                        }

                    } catch (FileNotFoundException e) {
                        throw new DosyaBulunamadiException();
                    }
                    break;

                case 7: // Programdan çık
                    scan.close();
                    System.out.println("\nProgram sonlandırılıyor.\n");
                    return;

                default:
                    System.out.println("\nGeçerli bir girdi girmediniz.");
            }
        }
    }

    public static void envanterEkleme(ArrayList<EnvanterKaydi> array, String isim, int stok) throws Exception{

        try {
            boolean varMi = false;
            for (EnvanterKaydi kayit : array) {
                if (kayit.getIsim().equals(isim)) {
                    kayit.setStok(stok);
                    varMi = true;
                    break;
                }
            }
            if (!varMi) {
                array.add(new EnvanterKaydi(isim, stok));
            }   
        } catch (Exception e) {
            throw new OzellikHatasiException();
        }  
    }

}

