package envanter;

import java.util.Scanner;
import java.util.ArrayList;
import java.io.ObjectOutputStream;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.InputStream;
import java.io.ObjectInputStream;

public class Main {
    public static void main(String[] args) throws Exception {

        Scanner userInput = new Scanner(System.in);
        ArrayList<EnvanterKaydi> esyalar = new ArrayList<>();
        while(true) {
            System.out.println("Envanter Takip Sistemine hoş geldiniz. Ne yapmak istersiniz?");
            System.out.println("1-) Eşya Ekleme 2-) Eşya Çıkarma 3-) Stok Güncelleme 4-)Envanter Listeleme 5-) Dosyaya Kaydetme 6-) Dosyadan Geri Yükleme 7-) Çıkış");
            int answer = userInput.nextInt();
            if (answer == 7){
                break;
            }
            else if (answer == 1) {
                System.out.println("Eklemek istediğiniz eşyanın adı nedir?");
                String itemName = userInput.next();
                System.out.println("Eşyanın stok miktarı?");
                int stockAmount = 0;
                try {
                    stockAmount = userInput.nextInt();
                } catch (Exception e) {
                    throw new OzellikHatasiException("HATA: Eşya bilgileri hatalı veya eksik.");
                }
                if (doesExist(itemName, esyalar) == -1) {
                    esyalar.add(new EnvanterKaydi(itemName, stockAmount));
                    System.out.println("Eşyanız envantere eklenmiştir.");
                    System.out.println();
                }
                else {
                    esyalar.get(doesExist(itemName, esyalar)).stokGuncelle(stockAmount);
                    System.out.println("Eşyanız zaten var olduğu için stok sayısı güncellenmiştir.");
                    System.out.println();
                }
            }
            else if (answer == 2) {
                System.out.println("Eşyanızın adı nedir?");
                String itemName = userInput.next();
                if (doesExist(itemName, esyalar) == -1) throw new EsyaYokException("HATA: İstenen eşya envanterde mevcut değil.");
                else {
                    esyalar.remove(doesExist(itemName, esyalar));
                    System.out.println("Eşya silinmiştir.");
                    System.out.println();
                }
            }
            else if (answer == 3) {
                System.out.println("Stok miktarını güncellemek istediğiniz eşyanın adını girin.");
                String itemName = userInput.next();
                if (doesExist(itemName, esyalar) == -1) throw new EsyaYokException("HATA: İstenen eşya envanterde mevcut değil.");
                else {
                    System.out.println("Miktarı girin.");
                    int stockAmount = userInput.nextInt();
                    if (stockAmount < 0) throw new StokNegatifException("HATA: Stok miktarı negatif olamaz.");
                    else {
                        esyalar.get(doesExist(itemName, esyalar)).stokGuncelle(stockAmount);
                        System.out.println("Stok miktarı güncellenmiştir.");
                        System.out.println();
                    }
                }
            }
            else if (answer == 4) {
                for (int i = 0; i < esyalar.size(); i++) {
                    System.out.println("İsim: " + esyalar.get(i).getIsim() + " Stok: " + esyalar.get(i).getStok());
                }
                System.out.println();
            }
            else if (answer == 5) {
                ObjectOutputStream Output = new ObjectOutputStream(new FileOutputStream("envanter.bin"));
                Output.writeObject(esyalar);
                System.out.println("Envanter bilgileri kaydedildi.");
                System.out.println();
                Output.close();
            }
            else if (answer == 6) {
                boolean isempty = false;
                try {
                    ObjectInputStream Input = new ObjectInputStream(new FileInputStream("envanter.bin"));
                    Object aaa = Input.readObject();
                    esyalar = (ArrayList<EnvanterKaydi>) aaa;                   
                    System.out.println();
                    Input.close();
                }
                catch (Exception e) {
                    throw new DosyaBulunamadiException("HATA: envanter dosyası bulunamadı.");
                }
                if (esyalar.isEmpty()) isempty = true;
                else isempty = false;
                if (isempty) {
                    throw new BosDosyaException("HATA: envanter dosyası boş.");
                }
                System.out.println("Envanter yüklenildi.");
            }
        }
        userInput.close();
    }

    public static int doesExist(String itemName, ArrayList<EnvanterKaydi> esyalar) {
        for (int i = 0; i < esyalar.size(); i++) {
            if (esyalar.get(i).getIsim().equals(itemName)) return i;
        }
        return -1;
    }
}

