import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IO;
import java.io.IOException;
import java.io.ObjectInputStream;
import java.io.ObjectOutputStream;
import java.util.ArrayList;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scan = new Scanner(System.in);
        System.out.println("ENVANTER TAKIP SISTEMI\n");
        System.out.println(
                "(1)Esya Ekleme\n(2)Esya Cikarma\n(3)Stok Guncelleme\n(4)Envanter Listeleme\n(5)Dosya Kaydetme\n(6)Dosyadan Geri Yukleme\n(-1)Cikis");
        ArrayList<EnvanterKaydi> envanterler = new ArrayList<>();
        File file = null;
        int scanned;
        while (true) {
            // scan = new Scanner(System.in);
            scanned = scan.nextInt();
            if (scanned == 1) {
                esyaEkleme(envanterler);
            } else if (scanned == 2) {
                esyaCikarma(envanterler);
            } else if (scanned == 3) {
                stokGuncelleme(envanterler);
            } else if (scanned == 4) {
                envanterListeleme(envanterler);
            } else if (scanned == 5) {
                dosyayaKaydetme(file, envanterler);
            } else if (scanned == 6) {
                dosyadanGeriYukleme(file, envanterler);

            } else if (scanned == -1) {

                break;
            } else {
                System.out.println("Lutfen verilen numaralardan girin");
            }
            scan.nextLine();
        }
        scan.close();
    }

    public static void dosyadanGeriYukleme(File file, ArrayList<EnvanterKaydi> envanterler) {
        try {
            file = new File("envanterler.bin");
            if (!file.exists()) {
                throw new DosyaBulunamadiException();
            }
            FileInputStream in = new FileInputStream(file);
            ObjectInputStream objin = new ObjectInputStream(in);
            if (objin.available() <= 0) {
                objin.close();
                throw new BosDosyaException();
            }

            while (objin.available() > 0) {
                EnvanterKaydi en = (EnvanterKaydi) objin.readObject();
                System.out.println("isim: ");
                System.out.print(en.getIsim());
                System.out.print(" Stok: ");
                System.out.print(en.getStok());
                envanterler.add(en);
            }
            objin.close();

        } catch (DosyaBulunamadiException e) {
            System.out.println(e.getMessage());
        } catch (BosDosyaException e) {
            System.out.println(e.getMessage());
        } catch (ClassNotFoundException e) {
            System.out.println("Class not found");
        } catch (IOException e) {
            System.out.println("IOexception");
        }
    }

    public static void dosyayaKaydetme(File file, ArrayList<EnvanterKaydi> envanterler) {
        try {
            file = new File("envanterler.bin");
            FileOutputStream out = new FileOutputStream(file);
            ObjectOutputStream objout = new ObjectOutputStream(out);
            for (int i = 0; i < envanterler.size(); i++) {
                objout.writeObject(envanterler.get(i));
            }
            objout.flush();
            objout.close();
        } catch (IOException e) {
            System.out.println("IOexception");
        }
    }

    public static void envanterListeleme(ArrayList<EnvanterKaydi> envanterler) {
        for (int i = 0; i < envanterler.size(); i++) {
            EnvanterKaydi en = envanterler.get(i);
            System.out.println("isim: ");
            System.out.print(en.getIsim());
            System.out.print(" Stok: ");
            System.out.print(en.getStok());
        }
    }

    public static void esyaEkleme(ArrayList<EnvanterKaydi> envanterler) {
        Scanner scan = new Scanner(System.in);
        System.out.println("isim: ");
        String name = scan.next();
        System.out.println("Stok: ");
        int stok = scan.nextInt();
        System.out.println();
        scan.close();
        if (esyaNerde(name, envanterler) == -1) {
            envanterler.add(new EnvanterKaydi(name, stok));
        } else {
            envanterler.get(esyaNerde(name, envanterler)).stokGuncelle(stok);
        }
    }

    public static void esyaCikarma(ArrayList<EnvanterKaydi> envanterler) {
        try {
            Scanner scan = new Scanner(System.in);
            System.out.println("isim: ");
            String name = scan.next();
            scan.close();
            if (esyaNerde(name, envanterler) == -1) {
                throw new EsyaYokException();
            } else {
                envanterler.remove(esyaNerde(name, envanterler));
            }
        } catch (EsyaYokException e) {
            System.out.println(e.getMessage());
        }
    }

    public static void stokGuncelleme(ArrayList<EnvanterKaydi> envanterler) {
        try {
            Scanner scan = new Scanner(System.in);
            System.out.println("isim: ");
            String name = scan.next();
            if (esyaNerde(name, envanterler) == -1) {
                scan.close();
                throw new EsyaYokException();
            }
            System.out.println("Yeni Stok: ");
            int stok = scan.nextInt();
            System.out.println();
            envanterler.get(esyaNerde(name, envanterler)).stokGuncelle(stok);
            scan.close();
        } catch (EsyaYokException e) {
            System.out.println(e.getMessage());
        }
    }

    public static int esyaNerde(String isim, ArrayList<EnvanterKaydi> envanterler) {
        for (int i = 0; i < envanterler.size(); i++) {
            EnvanterKaydi en = envanterler.get(i);
            if (en.getIsim().equals(isim)) {
                return i;
            }
        }
        return -1;
    }

}

