import java.io.*;
import java.util.*;

class EnvanterKaydi {
    private String isim;
    private int stok;

    public EnvanterKaydi(String isim, int stok) {
        this.isim = isim;
        this.stok = stok;
    }

    public String getIsim() {
        return isim;
    }

    public int getStok() {
        return stok;
    }

    public void setStok(int stok) {
        this.stok = stok;
    }

    public void stockUpdate(int miktar) throws StokNegatifException {
        if (this.stok + miktar < 0) {
            throw new StokNegatifException();
        }
        this.stok += miktar;
    }
}

class EnvanterYonetimi {
    private ArrayList<EnvanterKaydi> envanter = new ArrayList<>();

    public void esyaEkle(String isim, int stok) {
        for (EnvanterKaydi kayit : envanter) {
            if (kayit.getIsim().equals(isim)) {
                try {
                    kayit.stockUpdate(stok);
                } catch (StokNegatifException e) {
                    System.err.println(e.getMessage());
                }
                return;
            }
        }
        envanter.add(new EnvanterKaydi(isim, stok));
    }

    public void deleteItem(String isim) throws EsyaYokException {
        int index = -1;
        for (int i = 0; i < envanter.size(); i++) {
            if (envanter.get(i).getIsim().equals(isim)) {
                index = i;
                break;
            }
        }
        if (index != -1) {
            envanter.remove(index);
        } else {
            throw new EsyaYokException();
        }
    }
    public void stokGuncelle(String isim, int miktar) throws EsyaYokException, StokNegatifException {
        for (EnvanterKaydi kayit : envanter) {
            if (kayit.getIsim().equals(isim)) {
                kayit.stockUpdate(miktar);
                return;
            }
        }
        throw new EsyaYokException();
    }

    public void ListInventory() {
        if (envanter.isEmpty()) {
            System.out.println("Envanter boş.");
        } else {
            for (EnvanterKaydi kayit : envanter) {
                System.out.println(kayit.getIsim() + " - Stok: " + kayit.getStok());
            }
        }
    }

    public void saveInventory(String dosyaAdi) {
        try (PrintWriter writer = new PrintWriter(new FileWriter(dosyaAdi))) {
            for (EnvanterKaydi kayit : envanter) {
                writer.println(kayit.getIsim() + " " + kayit.getStok());
            }
        } catch (IOException e) {
            System.err.println("Write error: " + e.getMessage());
        }
    }

    public void envanteriYukle(String dosyaAdi) throws DosyaBulunamadiException, BosDosyaException, OzellikHatasiException {
        File file = new File(dosyaAdi);
        if (!file.exists()) {
            throw new DosyaBulunamadiException(dosyaAdi);
        }
        try (BufferedReader reader = new BufferedReader(new FileReader(file))) {
            String line;
            envanter.clear();
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(" ");
                if (parts.length == 2) {
                    try {
                        esyaEkle(parts[0], Integer.parseInt(parts[1]));
                    } catch (Exception e) {
                        throw new OzellikHatasiException();
                    }
                } else {
                    throw new OzellikHatasiException();
                }
            }
            if (envanter.isEmpty()) {
                throw new BosDosyaException(dosyaAdi);
            }
        } catch (IOException e) {
            System.err.println("Read error: " + e.getMessage());
        }
    }
}

